import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Item from './Item';
import '../styles/app.css';

function Dashboard({ onLogout }) {
    // Estados y hooks
    const navigate = useNavigate();
    const [text, setText] = useState("");
    const [todo, setTodo] = useState([]);
    const [isUpdating, setUpdating] = useState("");
    const [loading, setLoading] = useState(false);
    const [isSearchFocused, setIsSearchFocused] = useState(false);
    const [searchQuery, setSearchQuery] = useState("");

    // Autenticación del usuario
    useEffect(() => {
        const token = localStorage.getItem("token");
        if (!token) {
            navigate("/login");
        }
    }, [navigate]);

    // Obtener tareas
    useEffect(() => {
        const fetchTodos = async () => {
            const token = localStorage.getItem("token");
            if (token) {
                setLoading(true);
                try {
                    const res = await axios.get("http://localhost:5000/get-todo", {
                        headers: { Authorization: token },
                    });
                    setTodo(res.data);
                } catch (err) {
                    console.error("Error fetching todos:", err);
                    if (err.response?.status === 401) {
                        localStorage.removeItem("token");
                        navigate("/login");
                    }
                } finally {
                    setLoading(false);
                }
            }
        };
        fetchTodos();
    }, [navigate]);

    // Manejo de tareas (CRUD)
    const addUpdateTodo = () => {
        const token = localStorage.getItem("token");
        if (!token) {
            alert("No estás autenticado");
            navigate("/login");
            return;
        }

        if (!isUpdating) {
            // Agregar tarea
            axios.post(
                "http://localhost:5000/save-todo",
                { text },
                { headers: { Authorization: token } }
            )
                .then((res) => {
                    setTodo((prev) => [...prev, res.data]);
                    setText("");
                })
                .catch((err) => console.error("Error adding todo:", err));
        } else {
            // Actualizar tarea
            axios.post(
                "http://localhost:5000/update-todo",
                { _id: isUpdating, text },
                { headers: { Authorization: token } }
            )
                .then((res) => {
                    setTodo((prev) =>
                        prev.map((item) => (item._id === isUpdating ? res.data : item))
                    );
                    setText("");
                    setUpdating("");
                })
                .catch((err) => console.error("Error updating todo:", err));
        }
    };

    const deleteTodo = (_id) => {
        const token = localStorage.getItem("token");
        if (!token) {
            alert("No estás autenticado");
            navigate("/login");
            return;
        }

        axios.post(
            "http://localhost:5000/delete-todo",
            { _id },
            { headers: { Authorization: token } }
        )
            .then(() => {
                setTodo((prev) => prev.filter((item) => item._id !== _id));
            })
            .catch((err) => console.error(err));
    };

    const updateTodo = (_id, text) => {
        setUpdating(_id);
        setText(text);
    };

    // Manejo de clases dinámicas
    const handleFocus = () => setIsWide(true);
    const handleBlur = () => setIsWide(false);

    // Renderizado
    return (
        <section className={`App ${isSearchFocused ? "wide" : ""}`}>
            <div className="search">
                <input
                    type="text"
                    placeholder="Buscar..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    onFocus={handleFocus}
                    onBlur={handleBlur}
                />
            </div>

            {/* Contenido principal */}
            <main className={`main-content ${isWide ? "wide" : ""}`}>
                <div className="container">
                    {loading ? (
                        <p>Cargando tareas...</p>
                    ) : (
                        <>
                            {/* Entrada de nueva tarea */}
                            <div className="top">
                                <input
                                    type="text"
                                    placeholder="Escribe algo..."
                                    value={text}
                                    onChange={(e) => setText(e.target.value)}
                                />
                                <button className="add" onClick={addUpdateTodo}>
                                    {isUpdating ? "Actualizar" : "Agregar"}
                                </button>
                            </div>

                            {/* Lista de tareas */}
                            <div className="list">
                                {todo.map((item) => (
                                    <Item
                                        key={item._id}
                                        text={item.text}
                                        remove={() => deleteTodo(item._id)}
                                        update={() => updateTodo(item._id, item.text)}
                                    />
                                ))}
                            </div>
                        </>
                    )}
                </div>
            </main>

            {/* Botón de logout */}
            <footer className="footer">
                <button className="logout-button" onClick={onLogout}>
                    Cerrar sesión
                </button>
            </footer>
        </section>
    );
}

export default Dashboard;
