import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import '../app.css';

import Item from './Item';

function Dashboard({ onLogout }) { // Accept onLogout as a prop
  const navigate = useNavigate(); // Use useNavigate for navigation
  const [text, setText] = useState("");
  const [todo, setTodo] = useState([]);

  const [isUpdating, setUpdating] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchTasks = async () => {
      const token = localStorage.getItem("token");
      if (!token) {
        navigate("/login");
        return;
      }
      const response = await fetch("http://localhost:5000/api/tasks", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      setTasks(data);
    };
    fetchTasks();
  }, []);

  // Check if the user is authenticated

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login"); // Redirect to login if no token
    }

  }, [navigate]);

  // Fetch todos
  useEffect(() => {
    const fetchTodos = async () => {
      const token = localStorage.getItem("token");
      if (token) {
        setLoading(true);
        try {
          const res = await axios.get("http://localhost:5000/get-todo", {
            headers: { Authorization: token },
          });
          setTodo(res.data);
        } catch (err) {
          console.error("Error fetching todos:", err);
          if (err.response?.status === 401) {
            localStorage.removeItem("token"); // Remove invalid token
            navigate("/login"); // Redirect to login
          }
        } finally {
          setLoading(false);
        }
      }
    };
    fetchTodos();
  }, [navigate]);

  const addUpdateTodo = () => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("No estás autenticado");
      navigate("/login");
      return;
    }

    if (isUpdating === "") {
      axios.post(
        "http://localhost:5000/save-todo",
        { text },
        { headers: { Authorization: token } }
      )
        .then((res) => {
          console.log("Todo added:", res.data);
          setText("");
        })
        .catch((err) => console.error("Error adding todo:", err));
    } else {
      axios.post(
        "http://localhost:5000/update-todo",
        { _id: isUpdating, text },
        { headers: { Authorization: token } }
      )
        .then((res) => {
          console.log("Todo updated:", res.data);
          setText("");
          setUpdating("");
        })
        .catch((err) => console.error("Error updating todo:", err));
    }
  };

  const deleteTodo = (_id) => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("No estás autenticado");
      navigate("/login");
      return;
    }

    axios.post(
      "http://localhost:5000/delete-todo",
      { _id },
      { headers: { Authorization: token } }
    )
      .then((res) => console.log(res.data))
      .catch((err) => console.error(err));
  };

  const updateTodo = (_id, text) => {
    setUpdating(_id);
    setText(text);
  };

  return (
    <div className="App">
      <div className="container">
        <h1>ToDo App</h1>
        <button onClick={onLogout}>Logout</button> {/* Logout button */}
        {loading ? (
          <p>Cargando tareas...</p>
        ) : (
          <>
            <div className="top">
              <input
                type="text"
                placeholder="Write Something..."
                value={text}
                onChange={(e) => setText(e.target.value)}
              />
              <div className="add" onClick={addUpdateTodo}>
                {isUpdating ? "Update" : "Add"}
              </div>
            </div>

            <div className="list">
              {todo.map((item) => (
                <Item
                  key={item._id}
                  text={item.text}
                  remove={() => deleteTodo(item._id)}
                  update={() => updateTodo(item._id, item.text)}
                />
              ))}
            </div>
          </>
        )}
      </div>
      </div>
  );
}

export default Dashboard;
