import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Item from './Item';
import '../styles/app.css';

function Dashboard({ onLogout }) {
    const navigate = useNavigate();
    const [text, setText] = useState("");
    const [todo, setTodo] = useState([]);
    const [isUpdating, setUpdating] = useState("");
    const [loading, setLoading] = useState(false);
    const [isWide, setIsWide] = useState(false);

    useEffect(() => {
        const token = localStorage.getItem("token");
        if (!token) {
            navigate("/login");
        }
    }, [navigate]);

    useEffect(() => {
        const fetchTodos = async () => {
            const token = localStorage.getItem("token");
            if (token) {
                setLoading(true);
                try {
                    const res = await axios.get("http://localhost:5000/get-todo", {
                        headers: { Authorization: token },
                    });
                    setTodo(res.data);
                } catch (err) {
                    console.error("Error fetching todos:", err);
                    if (err.response?.status === 401) {
                        localStorage.removeItem("token");
                        navigate("/login");
                    }
                } finally {
                    setLoading(false);
                }
            }
        };
        fetchTodos();
    }, [navigate]);

    const handleFocus = () => setIsWide(true);
    const handleBlur = () => setIsWide(false);

    const addUpdateTodo = () => {
        const token = localStorage.getItem("token");
        if (!token) {
            alert("No estás autenticado");
            navigate("/login");
            return;
        }

        if (!isUpdating) {
            axios.post("http://localhost:5000/save-todo", { text }, { headers: { Authorization: token } })
                .then((res) => {
                    setTodo((prev) => [...prev, res.data]);
                    setText("");
                })
                .catch((err) => console.error("Error adding todo:", err));
        } else {
            axios.post("http://localhost:5000/update-todo", { _id: isUpdating, text }, { headers: { Authorization: token } })
                .then((res) => {
                    setTodo((prev) => prev.map((item) => (item._id === isUpdating ? res.data : item)));
                    setText("");
                    setUpdating("");
                })
                .catch((err) => console.error("Error updating todo:", err));
        }
    };

    const deleteTodo = (_id) => {
        const token = localStorage.getItem("token");
        if (!token) {
            alert("No estás autenticado");
            navigate("/login");
            return;
        }

        axios.post("http://localhost:5000/delete-todo", { _id }, { headers: { Authorization: token } })
            .then(() => {
                setTodo((prev) => prev.filter((item) => item._id !== _id));
            })
            .catch((err) => console.error(err));
    };

    const updateTodo = (_id, text) => {
        setUpdating(_id);
        setText(text);
    };

    return (
        <section className="">
            <header className={`header ${isWide ? "wide" : ""}`}>
                <h1>ToDo App</h1>
                <div className={`header-menu ${isWide ? "hidden" : ""}`}>
                    <a href="#home" className="is-active">Home</a>
                    <a href="#tasks">Tasks</a>
                    <a href="#profile">Profile</a>
                </div>
                <div className="search">
                    <input
                        type="search"
                        placeholder="Buscar..."
                        onFocus={handleFocus}
                        onBlur={handleBlur}
                    />
                </div>
            </header>

            <main>
                <div className="container">
                    {loading ? (
                        <p>Cargando tareas...</p>
                    ) : (
                        <>
                            <div className="top">
                                <input
                                    type="text"
                                    placeholder="Escribe algo..."
                                    value={text}
                                    onChange={(e) => setText(e.target.value)}
                                />
                                <button className="add" onClick={addUpdateTodo}>Agregar</button>
                            </div>
                            {todo.map((item) => (
                                <Item
                                    key={item._id}
                                    item={item}
                                    onDelete={deleteTodo}
                                    onUpdate={updateTodo}
                                />
                            ))}
                        </>
                    )}
                </div>
            </main>

            <footer className="footer">
                <button className="logout-button" onClick={onLogout}>Cerrar sesión</button>
            </footer>
        </section>
    );
}

export default Dashboard;
