import React, { useState } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import Login from './components/Login';
import Registration from './components/Register';
import Dashboard from './components/Dashboard';

function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const navigate = useNavigate();

    const handleLogin = () => {
        setIsLoggedIn(true);
        navigate("/dashboard");
    };

    const handleLogout = () => {
        localStorage.removeItem("token"); // Clear user token from local storage
        setIsLoggedIn(false); // Update state to indicate user is logged out
        navigate("/"); // Redirect to home or login page
    };

    return (
        <>
            <Routes>
                <Route path="/" element={isLoggedIn ? <Dashboard onLogout={handleLogout} /> : <Login onLogin={handleLogin} />} />
                <Route path="/register" element={!isLoggedIn ? <Registration /> : <Dashboard onLogout={handleLogout} />} />
                <Route path="/dashboard" element={isLoggedIn ? <Dashboard onLogout={handleLogout} /> : <Login onLogin={handleLogin} />} />
            </Routes>
        </>
    );
}

export default App;
