import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Login from './components/Login'; // Your Login component
import Register from './components/Register'; // Your Register component
import Dashboard from './components/Dashboard'; // Your Dashboard component

function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false); // State to track login status

    const handleLogin = () => {
        setIsLoggedIn(true); // Update state to indicate user is logged in
    };

    return (
        <Router>
            <Routes>
                <Route path="/" element={<Login onLogin={handleLogin} />} /> {/* Pass handleLogin to Login */}
                <Route path="/register" element={<Register />} />
                <Route path="/dashboard" element={isLoggedIn ? <Dashboard /> : <Login onLogin={handleLogin} />} />
            </Routes>
        </Router>
    );
}

export default App;
