import React, { createContext, useContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser ] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem("token");
        if (token) {
            // Aquí puedes hacer una solicitud para obtener los datos del usuario
            axios.get("http://localhost:5000/user", {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            })
            .then(response => {
                setUser (response.data); // Suponiendo que la respuesta contiene los datos del usuario
            })
            .catch(() => {
                localStorage.removeItem("token"); // Eliminar el token si hay un error
                navigate("/login");
            });
        }
    }, [navigate]);

    const login = async (email, password) => {
        const response = await axios.post("http://localhost:5000/login", { email, password });
        const { token } = response.data;
        localStorage.setItem("token", token);
        setUser (response.data.user); // Suponiendo que la respuesta contiene los datos del usuario
        navigate("/dashboard");
    };

    const logout = () => {
        localStorage.removeItem("token");
        setUser (null);
        navigate("/login");
    };

    return (
        <AuthContext.Provider value={{ user, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    return useContext(AuthContext);
};
