import Item from './Item';
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import '../app.css';


function Dashboard({ onLogout }) { // Accept onLogout as a prop
  const navigate = useNavigate(); // Use useNavigate for navigation
  const [text, setText] = useState("");
  const [todo, setTodo] = useState([]);
  const [isUpdating, setUpdating] = useState("");
  const [loading, setLoading] = useState(false);


  // Check if the user is authenticated

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login"); // Redirect to login if no token
    }

  }, [navigate]);

  // Fetch todos
  useEffect(() => {
    const fetchTodos = async () => {
      const token = localStorage.getItem("token");
      if (token) {
        setLoading(true);
        try {
          const res = await axios.get("http://localhost:5000/get-todo", {
            headers: { Authorization: token },
          });
          setTodo(res.data);
        } catch (err) {
          console.error("Error fetching todos:", err);
          if (err.response?.status === 401) {
            localStorage.removeItem("token"); // Remove invalid token
            navigate("/login"); // Redirect to login
          }
        } finally {
          setLoading(false);
        }
      }
    };
    fetchTodos();
  }, [navigate]);

  const addUpdateTodo = (_id, text) => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("No est치s autenticado");
      navigate("/login");
      return;
    }

    if (isUpdating === "") {
      axios.post(
        "http://localhost:5000/save-todo",
        { text },
        { headers: { Authorization: token } }
      )
        .then((res) => {
          setTodo((prev) => [...prev, res.data]); // Agrega la nueva tarea al estado local
          setText("");
        })
        .catch((err) => console.error("Error adding todo:", err));
    } else {
      axios.post(
        "http://localhost:5000/update-todo",
        { _id: isUpdating, text },
        { headers: { Authorization: token } }
      )
        .then((res) => {
          setTodo((prev) =>
            prev.map((item) => (item._id === isUpdating ? res.data : item))
          ); // Actualiza la tarea en el estado local
          setText("");
          setUpdating("");
        })
        .catch((err) => console.error("Error updating todo:", err));
    }
  };

  const deleteTodo = (_id) => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("No est치s autenticado");
      navigate("/login");
      return;
    }

    axios.post(
      "http://localhost:5000/delete-todo",
      { _id },
      { headers: { Authorization: token } }
    )
      .then(() => {
        setTodo((prev) => prev.filter((item) => item._id !== _id)); // Elimina la tarea del estado local
      })
      .catch((err) => console.error(err));
  };


  return (
    <div className="App">
      <div className="container">
        <h1>ToDo App</h1>

        {loading ? (
          <p>Cargando tareas...</p>
        ) : (
          <>
            <div className="top">
              <input
                type="text"
                placeholder="Write Something..."
                value={text}
                onChange={(e) => setText(e.target.value)}
              />
              <div className="add" onClick={addUpdateTodo}>
                {isUpdating ? "Update" : "Add"}
              </div>
            </div>

            <div className="list">
              {todo.map((item) => (
                <Item
                  key={item._id}
                  text={item.text}
                  remove={() => deleteTodo(item._id)}
                  update={() => updateTodo(item._id, item.text)}
                />
              ))}
            </div>
          </>
        )}
      </div>
      <button className="logout-button" onClick={onLogout}>
        Logout  
      </button>

    </div>
  );
}

export default Dashboard;
