import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import '../app.css';

import Item from './Item';

function Dashboard({ onLogout }) { // Accept onLogout as a prop
  const navigate = useNavigate(); // Use useNavigate for navigation
  const [text, setText] = useState("");
  const [todo, setTodo] = useState([]);
  const [isUpdating, setUpdating] = useState("");
  const [loading, setLoading] = useState(false);


  // Check if the user is authenticated

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login"); // Redirect to login if no token
    }

  }, [navigate]);

  // Fetch todos
  useEffect(() => {
    const fetchTodos = async () => {
      const token = localStorage.getItem("token");
      if (token) {
        setLoading(true);
        try {
          const res = await axios.get("http://localhost:5000/get-todo", {
            headers: { Authorization: token },
          });
          setTodo(res.data);
        } catch (err) {
          console.error("Error fetching todos:", err);
          if (err.response?.status === 401) {
            localStorage.removeItem("token"); // Remove invalid token
            navigate("/login"); // Redirect to login
          }
        } finally {
          setLoading(false);
        }
      }
    };
    fetchTodos();
  }, [navigate]);


  return (
    <div className="App">
      <div className="container">
        <h1>ToDo App</h1>
        <button onClick={onLogout}>Logout</button> {/* Logout button */}
        {loading ? (
          <p>Cargando tareas...</p>
        ) : (
          <>
            <div className="top">
              <input
                type="text"
                placeholder="Write Something..."
                value={text}
                onChange={(e) => setText(e.target.value)}
              />
              <div className="add" onClick={addUpdateTodo}>
                {isUpdating ? "Update" : "Add"}
              </div>
            </div>

            <div className="list">
              {todo.map((item) => (
                <Item
                  key={item._id}
                  text={item.text}
                  remove={() => deleteTodo(item._id)}
                  update={() => updateTodo(item._id, item.text)}
                />
              ))}
            </div>
          </>
        )}
      </div>
      </div>
  );
}

export default Dashboard;
