import React from "react";
import{ useState, useEffect } from 'react';
import axios from "axios";

import './app.css';
import Item from './components/Item'

function App() {

  const [text, setText] = useState("");
  const [todo, setTodo] = useState([]);
  const [isUpdating, setUpdating] = useState("");

  useEffect(() => {
    axios.get("http://localhost:5000/get-todo")
      .then((res) => setTodo(res.data))
      .catch((err) => console.log("Error al obtener todos:",err));
  }, []); // Dependencias vacías


  const addUpdateTodo = () => {
    if (isUpdating === "") {
      axios.post("http://localhost:5000/save-todo", { text })
        .then((res) => {
          console.log("Todo añadido:", res.data);
          setText("");
        })
        .catch((err) => console.error("Error al añadir todo:", err));
    } else {
      axios.post("http://localhost:5000/update-todo", { _id: isUpdating, text })
        .then((res) => {
          console.log("Todo actualizado:", res.data);
          setText("");
          setUpdating("");
        })
        .catch((err) => console.error("Error al actualizar todo:", err));
    }
  };


  const deleteTodo = (_id) => {
    axios.post("http://localhost:5000/delete-todo", { _id })
      .then((res) => console.log(res.data))
      .catch((err) => console.log(err));
  }

  const updateTodo = (_id, text) => {
    setUpdating(_id);
    setText(text);
  }

  const [loading, setLoading] = useState(false);

useEffect(() => {
  setLoading(true);
  axios.get("http://localhost:5000/get-todo")
    .then((res) => setTodo(res.data))
    .catch((err) => console.error("Error al obtener todos:", err))
    .finally(() => setLoading(false));
}, []);

return (
  <div className="App">
    <div className="container">
      <h1>ToDo App</h1>
      {loading ? (
        <p>Cargando tareas...</p>
      ) : (
        <>
          <div className="top">
            <input
              type="text"
              placeholder='Write Something...'
              value={text}
              onChange={(e) => setText(e.target.value)} />
            <div className="add"
              onClick={addUpdateTodo}>{isUpdating ? "Update" : "Add"}</div>
          </div>

          <div className="list">
            {todo.map(item => <Item
              key={item._id}
              text={item.text}
              remove={() => deleteTodo(item._id)}
              update={() => updateTodo(item._id, item.text)} />)}
          </div>
        </>
      )}
    </div>
  </div>
);

}

export default App;
