import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import axios from 'axios';

import './app.css';
import Item from './components/Item';
import Login from './components/Login';

function App() {
  const [text, setText] = useState("");
  const [todo, setTodo] = useState([]);
  const [isUpdating, setUpdating] = useState("");
  const [loading, setLoading] = useState(false);

  // Obtener tareas al cargar el componente
  useEffect(() => {
    setLoading(true);
    axios.get("http://localhost:5000/get-todo")
      .then((res) => setTodo(res.data))
      .catch((err) => console.error("Error al obtener todos:", err))
      .finally(() => setLoading(false));
  }, []);

  // Agregar o actualizar tareas
  const addUpdateTodo = () => {
    if (isUpdating === "") {
      axios.post("http://localhost:5000/save-todo", { text })
        .then((res) => {
          console.log("Todo a単adido:", res.data);
          setText("");
          setTodo((prev) => [...prev, res.data]); // Agregar la nueva tarea al estado
        })
        .catch((err) => console.error("Error al a単adir todo:", err));
    } else {
      axios.post("http://localhost:5000/update-todo", { _id: isUpdating, text })
        .then((res) => {
          console.log("Todo actualizado:", res.data);
          setText("");
          setUpdating("");
          setTodo((prev) =>
            prev.map((item) => (item._id === isUpdating ? res.data : item))
          ); // Actualizar el estado con la tarea editada
        })
        .catch((err) => console.error("Error al actualizar todo:", err));
    }
  };

  // Eliminar tareas
  const deleteTodo = (_id) => {
    axios.post("http://localhost:5000/delete-todo", { _id })
      .then((res) => {
        console.log(res.data);
        setTodo((prev) => prev.filter((item) => item._id !== _id)); // Eliminar la tarea del estado
      })
      .catch((err) => console.log(err));
  };

  // Preparar tarea para actualizar
  const updateTodo = (_id, text) => {
    setUpdating(_id);
    setText(text);
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/login"
          element={<Login />}
        />
        <Route
          path="/"
          element={
            <div className="App">
              <div className="container">
                <h1>ToDo App</h1>
                {loading ? (
                  <p>Cargando tareas...</p>
                ) : (
                  <>
                    <div className="top">
                      <input
                        type="text"
                        placeholder="Write Something..."
                        value={text}
                        onChange={(e) => setText(e.target.value)}
                      />
                      <div className="add" onClick={addUpdateTodo}>
                        {isUpdating ? "Update" : "Add"}
                      </div>
                    </div>
                    <div className="list">
                      {todo.map((item) => (
                        <Item
                          key={item._id}
                          text={item.text}
                          remove={() => deleteTodo(item._id)}
                          update={() => updateTodo(item._id, item.text)}
                        />
                      ))}
                    </div>
                  </>
                )}
              </div>
            </div>
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
