import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Registration() {
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [errorMessage, setErrorMessage] = useState(""); // State for error messages
    const navigate = useNavigate(); // Use useNavigate for navigation

    const handleSubmit = async (e) => {
        e.preventDefault();
        setErrorMessage(""); // Clear previous error messages

        // Check if all fields are filled
        if (!username || !email || !password) {
            setErrorMessage("Todos los campos son obligatorios."); // Set error message
            return; // Prevent form submission
        }

        try {
            const response = await axios.post("http://localhost:5000/register", {
                username,
                email,
                password,
            });
            alert("Registro exitoso!"); // Notify user of successful registration
            navigate("/"); // Redirect to login page after successful registration
        } catch (error) {
            if (error.response && error.response.data) {
                setErrorMessage(error.response.data.message || "Error en el registro");
            } else {
                setErrorMessage("Ocurrió un error. Por favor, inténtelo de nuevo.");
            }
        }
    };

    return (
        <div>
            <h2>Registro</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Nombre de usuario"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Contraseña"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit">Registrarse</button>
            </form>
            {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>} {/* Display error message */}
        </div>
    );
}

export default Registration;
