import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";

import axios from "axios";
import { useNavigate } from "react-router-dom";

import './app.css';
import Item from './components/Item';
const history = useHistory();
function App() {
  const navigate = useNavigate(); // Cambiado de useHistory a useNavigate
  const [text, setText] = useState("");
  const [todo, setTodo] = useState([]);
  const [isUpdating, setUpdating] = useState("");
  const [loading, setLoading] = useState(false);

  // Verificar si el usuario está autenticado

// Reemplazar navigate("/ruta") con history.push("/ruta")
useEffect(() => {
  const token = localStorage.getItem("token");
  if (!token) {
    history.push("/login"); // Redirigir al login si no hay token
  }
}, [history]);
  // Obtener tareas protegidas
  useEffect(() => {
    const fetchTodos = async () => {
      const token = localStorage.getItem("token");
      if (token) {
        setLoading(true);
        try {
          const res = await axios.get("http://localhost:5000/get-todo", {
            headers: { Authorization: token }, // Enviar el token en los headers
          });
          setTodo(res.data);
        } catch (err) {
          console.error("Error al obtener todos:", err);
          if (err.response?.status === 401) {
            localStorage.removeItem("token"); // Eliminar token inválido
            navigate("/login"); // Redirigir al login
          }
        } finally {
          setLoading(false);
        }
      }
    };
    fetchTodos();
  }, [navigate]);

  const addUpdateTodo = () => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("No estás autenticado");
      navigate("/login");
      return;
    }

    if (isUpdating === "") {
      axios.post(
        "http://localhost:5000/save-todo",
        { text },
        { headers: { Authorization: token } }
      )
        .then((res) => {
          console.log("Todo añadido:", res.data);
          setText("");
        })
        .catch((err) => console.error("Error al añadir todo:", err));
    } else {
      axios.post(
        "http://localhost:5000/update-todo",
        { _id: isUpdating, text },
        { headers: { Authorization: token } }
      )
        .then((res) => {
          console.log("Todo actualizado:", res.data);
          setText("");
          setUpdating("");
        })
        .catch((err) => console.error("Error al actualizar todo:", err));
    }
  };

  const deleteTodo = (_id) => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("No estás autenticado");
      navigate("/login");
      return;
    }

    axios.post(
      "http://localhost:5000/delete-todo",
      { _id },
      { headers: { Authorization: token } }
    )
      .then((res) => console.log(res.data))
      .catch((err) => console.error(err));
  };

  const updateTodo = (_id, text) => {
    setUpdating(_id);
    setText(text);
  };

  return (
    <div className="App">
      <div className="container">
        <h1>ToDo App</h1>
        {loading ? (
          <p>Cargando tareas...</p>
        ) : (
          <>
            <div className="top">
              <input
                type="text"
                placeholder="Write Something..."
                value={text}
                onChange={(e) => setText(e.target.value)}
              />
              <div className="add" onClick={addUpdateTodo}>
                {isUpdating ? "Update" : "Add"}
              </div>
            </div>

            <div className="list">
              {todo.map((item) => (
                <Item
                  key={item._id}
                  text={item.text}
                  remove={() => deleteTodo(item._id)}
                  update={() => updateTodo(item._id, item.text)}
                />
              ))}
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default App;
