import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Item from './Item';
import '../styles/app.css';

function Dashboard({ onLogout }) {
    // Manejo de clases dinámicas
    const [isSearchFocused, setIsSearchFocused] = useState(false); // Controla si el buscador está activo
    const [searchQuery, setSearchQuery] = useState("");
    const handleFocus = () => setIsSearchFocused(true); // Activa el modo búsqueda
    const handleBlur = () => setIsSearchFocused(false);
    // Estados y hooks
    const navigate = useNavigate();
    const [text, setText] = useState("");
    const [todo, setTodo] = useState([]);
    const [isUpdating, setUpdating] = useState("");

    // Autenticación del usuario
    useEffect(() => {
        const token = localStorage.getItem("token");
        if (!token) {
            navigate("/login");
        }
    }, [navigate]);

    // Obtener tareas
    useEffect(() => {
        const fetchTodos = async () => {
            const token = localStorage.getItem("token");
            if (token) {
                setLoading(true);
                try {
                    const res = await axios.get("http://localhost:5000/get-todo", {
                        headers: { Authorization: token },
                    });
                    setTodo(res.data);
                } catch (err) {
                    console.error("Error fetching todos:", err);
                    if (err.response?.status === 401) {
                        localStorage.removeItem("token");
                        navigate("/login");
                    }
                } finally {
                    setLoading(false);
                }
            }
        };
        fetchTodos();
    }, [navigate]);

    // Manejo de tareas (CRUD)
    const addUpdateTodo = () => {
        const token = localStorage.getItem("token");
        if (!token) {
            alert("No estás autenticado");
            navigate("/login");
            return;
        }

        if (!isUpdating) {
            // Agregar tarea
            axios.post(
                "http://localhost:5000/save-todo",
                { text },
                { headers: { Authorization: token } }
            )
                .then((res) => {
                    setTodo((prev) => [...prev, res.data]);
                    setText("");
                })
                .catch((err) => console.error("Error adding todo:", err));
        } else {
            // Actualizar tarea
            axios.post(
                "http://localhost:5000/update-todo",
                { _id: isUpdating, text },
                { headers: { Authorization: token } }
            )
                .then((res) => {
                    setTodo((prev) =>
                        prev.map((item) => (item._id === isUpdating ? res.data : item))
                    );
                    setText("");
                    setUpdating("");
                })
                .catch((err) => console.error("Error updating todo:", err));
        }
    };

    const deleteTodo = (_id) => {
        const token = localStorage.getItem("token");
        if (!token) {
            alert("No estás autenticado");
            navigate("/login");
            return;
        }

        axios.post(
            "http://localhost:5000/delete-todo",
            { _id },
            { headers: { Authorization: token } }
        )
            .then(() => {
                setTodo((prev) => prev.filter((item) => item._id !== _id));
            })
            .catch((err) => console.error(err));
    };

    const updateTodo = (_id, text) => {
        setUpdating(_id);
        setText(text);
    };


    // Renderizado
    return (
        <section className="App">
        {/* Encabezado */}
        <header className={`header ${isWide ? "wide" : ""}`}>
            <h1>ToDo App</h1>
            <div className="header-menu">
                <a href="#home" className="is-active">Home</a>
                <a href="#tasks">Tasks</a>
                <a href="#profile">Profile</a>
            </div>
            <div className="search-bar">
                <input
                    type="search"
                    placeholder="Buscar..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)} // Captura texto
                    onFocus={handleFocus} // Clase dinámica al enfocar
                    onBlur={handleBlur} // Remueve clase al desenfocar
                />
            </div>
        </header>

        {/* Contenido principal */}
        <main className={`main-content ${isWide ? "wide" : ""}`}>
            <div className="container">
                {loading ? (
                    <p>Cargando tareas...</p>
                ) : (
                    <>
                        <div className="top">
                            <input
                                type="text"
                                placeholder="Escribe algo..."
                                value={searchQuery} // Aquí puedes usar el buscador para filtrar tareas
                                onChange={(e) => setSearchQuery(e.target.value)}
                            />
                            <button className="add" onClick={addUpdateTodo}>
                                Agregar
                            </button>
                        </div>

                        <div className="list">
                            {todo
                                .filter((item) =>
                                    item.text.toLowerCase().includes(searchQuery.toLowerCase())
                                )
                                .map((item) => (
                                    <div key={item._id}>
                                        <span>{item.text}</span>
                                        <button onClick={() => deleteTodo(item._id)}>Eliminar</button>
                                        <button onClick={() => updateTodo(item._id, item.text)}>Actualizar</button>
                                    </div>
                                ))}
                        </div>
                    </>
                )}
            </div>
        </main>

        <footer className="footer">
            <button className="logout-button" onClick={onLogout}>
                Cerrar sesión
            </button>
        </footer>
    </section>
);
}

export default Dashboard;
